class Solution {

    int arr[];
    Random rand;
    int cumulativeSum;
    int rectangles[][];
    public Solution(int[][] rects) {
        rectangles = rects;
        rand = new Random();
        int n = rects.length;
        arr = new int[n];
        cumulativeSum = 0;
        for(int i=0;i<n;i++) {
            int each[] = rects[i];
			// finding the number of points within this rectangle 
            int curPoints = (each[2] - each[0] + 1) * (each[3] - each[1] + 1);

			// It will be easier to select index if we maintain cumulative probabilities.
			// Because binary search works on sorted array. 
			// If we maintain only curPoints, then we need to sort that array. Then it takes a lot of time.
			// If we maintain cumulative curPoints then it will always be in sorted manner and then difference between two adjacent curPoints is more, then then there are more chances to get that index.
            cumulativeSum += curPoints;
            arr[i] = cumulativeSum;
        }
    }

    public int[] pick() {

		// Finding random integer between [1, cumulativeSum]
        int randSum = rand.nextInt(cumulativeSum + 1);

        int l = 0;
        int r = arr.length;

		// Picking the upper bound
        while(l < r) {
            int m = l + (r-l)/2;

            if(arr[m] > randSum)
                r = m;
            else
                l = m+1;
        }

        int pickedRect[] = l < arr.length ? rectangles[l]: rectangles[l-1];

        int bl = pickedRect[0];
        int br = pickedRect[1];
        int tl = pickedRect[2];
        int tr = pickedRect[3];

		// Picking the random point within the chosen rectangle
		// We need to choose a point , let it be (x, y)
		// x can run from pickedRect[0] to pickedRect[2]
		// y can run from pickedRect[1] to pickedRect[3]
        return new int[]{bl + rand.nextInt(tl-bl+1), br + rand.nextInt(tr-br+1)};

    }
