from typing import Tuple
from random import randint
from bisect import bisect

class Rectangle:

    def __init__(self, x1: int, y1: int, x2: int, y2: int):
        self.x1 = x1
        self.y1 = y1
        self.x2 = x2
        self.y2 = y2

    def getRandomPoint(self) -> Tuple[int, int]:
        return (randint(self.x1, self.x2), randint(self.y1, self.y2))

    def getArea(self) -> int:
        return (self.x2 - self.x1 + 1) * (self.y2 - self.y1 + 1)

class Solution:

    def __init__(self, rects: List[List[int]]):
        self.rectangles = [Rectangle(x1, y1, x2, y2) for x1, y1, x2, y2 in rects]

        currentAreaSum = 0
        self.prefixAreaSum = []
        for rectangle in self.rectangles:
            currentAreaSum += rectangle.getArea()
            self.prefixAreaSum.append(currentAreaSum)

        self.totalAreaSum = self.prefixAreaSum[-1]


    def pick(self) -> List[int]:
        index = bisect(self.prefixAreaSum, randint(0, self.totalAreaSum))
        return self.rectangles[min(index, len(self.prefixAreaSum) - 1)].getRandomPoint()
