import bisect
import functools
class Solution:
    def mincostTickets(self, days: List[int], costs: List[int]) -> int:
        def find_next_day(start: int, no_of_days: int) -> int:
            next_idx = bisect.bisect_left(days, days[start] + no_of_days)

            # print (f'{next_idx} {days}')

            if next_idx < len(days):
                while next_idx - 1 >= 0 and days[next_idx] == days[next_idx - 1]:
                    next_idx -= 1

            next_days = next_idx if next_idx < len(days) else -1

            return next_days

        @functools.lru_cache(maxsize=None)
        def solve(start: int) -> int:
            if start < 0 or start >= len(days):
                return 0

            one_day = costs[0] + solve(start + 1 if start + 1 < len(days) else -1)

            next_days = find_next_day(start, 7)

            seven_days = costs[1] + solve(next_days) 

            next_days = find_next_day(start, 30)

            thiry_days = costs[2] + solve(next_days) 

            return min(one_day, seven_days, thiry_days)

        return solve(0)
