import random
class Solution:

    def numberOfPoints(self, rect):
        x1, y1, x2, y2 = rect
        return ((abs(x1-x2)+1) * (abs(y1-y2)+1)) 

    def sampleFromRect(self, rect):
        x1, y1, x2, y2 = rect
        x = random.randint(x1, x2) 
        y = random.randint(y1, y2) 
        return [x, y]

    def chooseRect(self):
        x = random.randint(0,self.n_points)
        left  = 0
        right = len(self.rects) - 1
        while left < right:
            mid = (left + right) // 2
            if x < self.prefix_sum[mid] :
                right = mid
            else:
                left = left +1
        return self.rects[left]

    def generatePrefixSum(self):
        self.prefix_sum = [0] * len(self.rects)
        self.prefix_sum[0] = self.numberOfPoints(self.rects[0])   
        for i in range(1,len(self.rects)):
            self.prefix_sum[i] = self.prefix_sum[i-1] + self.numberOfPoints(self.rects[i])   
        self.n_points = self.prefix_sum[-1]
        print(self.prefix_sum )


    def __init__(self, rects: List[List[int]]):
        self.rects = rects
        self.generatePrefixSum()

    def pick(self) -> List[int]:
        rect = self.chooseRect()
        return self.sampleFromRect(rect)

