class Solution {
    private TreeMap<Integer, Integer> map;
    private int[][] minmax;
    private int len;
    private int sum;

    public Solution(int[][] rects) {
        map = new TreeMap<>();
        len = rects.length;
        minmax = new int[len][4];
        sum = 0;
        for (int i = 0; i < len; i++) {
            minmax[i][0] = Math.min(rects[i][0], rects[i][2]);
            minmax[i][1] = Math.max(rects[i][0], rects[i][2]);
            minmax[i][2] = Math.min(rects[i][1], rects[i][3]);
            minmax[i][3] = Math.max(rects[i][1], rects[i][3]);
            sum += (rects[i][3] - rects[i][1] + 1) * (rects[i][2] - rects[i][0] + 1);
            map.put(sum, i);
        }
    }

    public int[] pick() {
        int rand = 1 + (int)(Math.random() * sum);
        int index = map.get(map.ceilingKey(rand));
        int[] rect = minmax[index];
        int xrange = rect[1] - rect[0] + 1;
        int yrange = rect[3] - rect[2] + 1;
        return new int[]{rect[0] + (int)(Math.random() * xrange), rect[2] + (int)(Math.random() * yrange)};
    }
}
