        uniform_int_distribution<int> Y(rect[1], rect[3]);

        return {X(generator), Y(generator)};
    }

private:
    vector<vector<int>> rects;

    vector<double> probabilities;

    int totalArea;

    random_device rd;

    mt19937 generator;

    discrete_distribution<int> distribution;
};

```

