from dataclasses import dataclass
from functools import cached_property
from itertools import accumulate
from random import choices, randint


@dataclass
class Rectangle:
    x: int
    y: int
    l: int
    b: int

    def generate(self) -> list[int]:
        return [
            self.x + randint(0, self.l),
            self.y + randint(0, self.b)
        ]

    @property
    def points(self) -> int:
        """
        :return: number of points inside and on boundary of the rectangle 
        """
        return (self.l + 1) * (self.b + 1)


class Solution:
    def __init__(self, rects: list[list[int]]):
        self.rects = [Rectangle(x1, y1, x2 - x1, y2 - y1) for x1, y1, x2, y2 in rects]

    def pick(self) -> list[int]:
        return self.select_rect().generate()

    def select_rect(self) -> Rectangle:
        return choices(self.rects, cum_weights=self.cum_weights, k=1)[0]

    @cached_property
    def cum_weights(self) -> list[float]:
        rect_points = [r.points for r in self.rects]
        total_points = sum(rect_points)
        return list(accumulate(points / total_points for points in rect_points))
