class Solution {
public:
   int total;
   vector<int>cum;
   vector<vector<int>>rects;
   int upper_bound(int target)
   {
       int low=0;
       int high=cum.size()-1;
       while(low<=high)
       {
           int mid=low+(high-low)/2;
           if (cum[mid]<=target) low=mid+1;
           else high=mid-1;
       }
       return low;
   }
    Solution(vector<vector<int>>& rects) 
    {
        this->rects=rects;
        total=0;
        for (int i=0;i<rects.size();i++)
       {
           total+=(rects[i][3]-rects[i][1]+1)*(rects[i][2]-rects[i][0]+1);
           cum.push_back(total);
       }
    }

    vector<int> pick() {
        int num=rand()%total;
        int ind=upper_bound(num);
        vector<int>&rect=rects[ind];
        int x_pts=rect[2]-rect[0]+1;
        int y_pts=rect[3]-rect[1]+1;
        int total_pts=x_pts*y_pts;
        int start=cum[ind]-total_pts;
        int offset=num-start;
        return {rect[0]+offset%x_pts,rect[1]+offset/x_pts};

    }
};

