    vector<int> pick() {
        int i = lower_bound(areas.begin(), areas.end(), rand() % 100) - areas.begin();
        int x = rand() % (rects[i][2] - rects[i][0] + 1) + rects[i][0];
        int y = rand() % (rects[i][3] - rects[i][1] + 1) + rects[i][1];
        return {x, y};
    }
};
