	class Solution 
	{
		protected int[][] units;
		protected int totalSum = 0;
		protected Random rand = new Random();

		public Solution(int[][] rects) 
		{
			// we can use the built-in random function just for one time
			// and as a result we no longer need the 'y2' value in the 'pick' method
			// and also we can take advantage of this fact

			units = rects;

			for (int[] theRec: rects)
			{    
				//theRec: [x1, y1, x2, y2]
				totalSum += (theRec[2] - theRec[0] + 1) * (theRec[3] - theRec[1] + 1);
				theRec[3] = totalSum;
				//theRec: [x1, y1, x2, totalSum]
			}

			++totalSum; // since rand.nextInt() is exclusive in the upper bound
		}

		protected int bisect_left(int target)
		{     
			int left = 0, right = units.length, mid;

			while (left < right)
			{         
				mid = (left + right) >> 1;   //(left + right) / 2

				if (units[mid][3] < target)
					left = mid + 1;
				else
					right = mid;
			}

			return left;
		}

		public int[] pick() 
		{
			int rnd = rand.nextInt(1, totalSum);
			int indx = bisect_left(rnd);
			int[] theUnit = units[indx];

			int a = rnd - (indx != 0 ? units[indx - 1][3] : 0) - 1;
			int b = theUnit[2] - theUnit[0] + 1;

			return new int[]
			{
				theUnit[0] + a % b,
				theUnit[1] + a / b
			};
		}
	}


