class Solution {
public:
    int n; //number of rectangles
    std::vector<std::vector<int>> myRects;
    std::vector<int> vArea;
    int total;

    int myBinarySearch(int x){
        // 1<=x<=total
        // return the smallest index i such that x<=vArea[i]
        if (x <= vArea[0]) return 0;

        int left = 0; 
        int right = vArea.size()-1;
        int mid;
        while (left<right){
            if (right-left==1) break;

            mid = (left+right)/2;
            if (vArea[mid] < x){
                left = mid;
            }else if (vArea[mid] >= x){
                right = mid;
            }
        }
        return right;
    }

    Solution(vector<vector<int>>& rects) {
        srand(time(NULL));
        myRects = rects;
        n = rects.size();
        int currArea;
        int accArea = 0;
        for (int i=0; i<n; i++){
            //currArea counts number of integral points in rects[i]
            currArea= (rects[i][2]-rects[i][0]+1)*(rects[i][3]-rects[i][1]+1);
            //accArea counts the accumulated number of integral points up to rects[i]
            accArea += currArea;
            vArea.push_back(accArea);
        }
    }

    vector<int> pick() {
        //randomly pick a rectangle in rects: rects[index]
        /* 
        Suppose vArea = {v0, v1, ..., vN=total},
        meaning that rects[0] has v0 integral points inside,
        rects[1] has (v1-v0) integral points inside and so on.

        We pick a random number 1<=r<=toal
        if (r=1,2,...,v0), then index=0
        if (r=v0+1,...,v1), then index=1
        if (r=v1+1,...,v2), then index=2 ...(and so on)
        */
        total = vArea.back();
        int r = 1 + (rand()%total);
        int index = myBinarySearch(r);
        //
        //randomly pick a point in rects[index]
        int a = myRects[index][0];
        int b = myRects[index][1];
        int x = myRects[index][2];
        int y = myRects[index][3];

        int xRand = a + rand()%(x-a+1);
        int yRand = b + rand()%(y-b+1);

        return std::vector<int>{xRand, yRand};
    }
};
