class Solution {
public:

    long long totalArea, rectNumber, totalRectangle, i;
    vector<vector<int >> rectangles;
    vector<long long> pref;
    Solution(vector<vector<int>> rects) {
        // we are given a rectangle and is asked to return a random point inside rectangle such that all points are equally likely to be returned

        // first we can think of using rand() pre defined function to generate a random number but here all the points or values are not equally likely to occur so
        // now we can think of generating points linearly..... here all the points will be equally likely to occur
        // what I meant by linearly generating points is
        // we will linearly iterate from 
        // 0 to (total area - 1) then again (0 to (total area - 1)) and so onn..
        // and then we will generate a point corresponding to that value


        i = -1; 
        totalRectangle = rects.size();
        rectNumber = 0;
        totalArea = 0;

        for(int j = 0; j < totalRectangle; j++){
            if(rects[j][0] > rects[j][2])
            swap(rects[j][0],rects[j][2]);

        if(rects[j][1] > rects[j][3])
            swap(rects[j][1],rects[j][3]);

            totalArea += (long long)(rects[j][2] - rects[j][0] + 1) * (rects[j][3] - rects[j][1] + 1);
            pref.push_back(totalArea);
        }


        rectangles = rects;
    }
    void swap(int &a, int &b)
    {
        int c = a;
        a = b;
        b = c;
    }
    vector<int> pick() {

        if(rectangles[rectNumber][0] > rectangles[rectNumber][2])
            swap(rectangles[rectNumber][0],rectangles[rectNumber][2]);

        if(rectangles[rectNumber][1] > rectangles[rectNumber][3])
            swap(rectangles[rectNumber][1],rectangles[rectNumber][3]);

        i++;
        long long j = i, area = pref[0];

        if(i == pref[rectNumber])
            rectNumber = (rectNumber+1 )%totalRectangle;

        if(rectNumber != 0){
           j -= pref[rectNumber - 1]; 
            area = pref[rectNumber] - pref[rectNumber - 1];
        }

        vector<int> v(2);

        v[0] = rectangles[rectNumber][0] + j/(rectangles[rectNumber][2]-rectangles[rectNumber][0] + 1);
        v[1] = rectangles[rectNumber][1]+ j%(rectangles[rectNumber][2]-rectangles[rectNumber][0] + 1);
        return v;
    }
};

/**
 * Your Solution object will be instantiated and called as such:
 * Solution* obj = new Solution(rects);
 * vector<int> param_1 = obj->pick();
 */
