	vector<vector<int>> rects;
	Solution(vector<vector<int>>& rects) {
		function<long long(vector<int>)> np=[&](vector<int> a){
			return 1LL*(a[2]-a[0]+1)*(a[3]-a[1]+1);
		};
		for(int i=0;i<rects.size();i++) {
			a.push_back(np(rects[i])+(a.size()==0?0:a.back()));
		}
		this->rects=rects;
	}

	vector<int> pick() {
		long long r=rand()%a.back();
		int l=upper_bound(a.begin(),a.end(),r)-a.begin();
		int kx=rects[l][0]+rand()%(rects[l][2]-rects[l][0]+1);
		int ky=rects[l][1]+rand()%(rects[l][3]-rects[l][1]+1);
		return {kx,ky};
	}
};

