        int rnd = rand() % v.back();
        int idx = upper_bound(v.begin(), v.end(), rnd) - v.begin();

        if (idx != 0) rnd -= v[idx - 1];
        // pick a random point in rects[idx]
        auto r = rects[idx];
        int x = rnd % (r[2] - r[0] + 1) + r[0];
        int y = rnd / (r[2] - r[0] + 1) + r[1];
        return { x, y };
