class Solution {
    int[] prefixSum;
    int n;
    int[][] rects;
    Random random;
    public Solution(int[][] rects) {
        this.n = rects.length;
        this.prefixSum = new int[n];
        this.rects = rects;
        this.random = new Random();

        for (var i=0; i<n; i++){
            prefixSum[i] = (rects[i][2] - rects[i][0] + 1) * (rects[i][3] - rects[i][1] + 1);
            if (i > 0){
                prefixSum[i] += prefixSum[i-1];
            }
        }
    }

    public int[] pick() {
        int[] picked = pickRectangle();
        int index = picked[0], target = picked[1];

        int a = rects[index][0];
        int b = rects[index][1];
        int x = rects[index][2];
        int y = rects[index][3];
        int n = target - (index == 0 ? 0 : prefixSum[index - 1]) - 1; // 0 <= n < area of the rectangle

        int width = x - a + 1;
        return new int[]{ a + n % width, b + n / width };
    }

    // return int[]{index, target}
    public int[] pickRectangle(){
        int target = random.nextInt(prefixSum[n-1]) + 1;
        int start = 0, end = n-1;
        if (prefixSum[start] >= target){
            return new int[]{start, target};
        }
        // result in (start, end]
        while (start < end){
            if (start + 1 == end){
                return new int[]{end, target};
            }
            int middle = start + (end - start) / 2;
            if (prefixSum[middle] >= target){
                end = middle;
            } else {
                start = middle;
            }
        }
        return new int[]{};
    }
}

