class Solution {
public:
    vector< int > m_rectangleAreas;
    vector< vector< int > > m_rectangles;

    Solution(vector<vector<int>>& rects) : m_rectangles( rects ) {
        int accumArea = 0;
        for ( int i = 0; i < rects.size(); ++i ) {
            const vector< int > & curRect = rects[ i ];
            const int dx = ( curRect[ 2 ] - curRect[ 0 ] ) + 1;
            const int dy = ( curRect[ 3 ] - curRect[ 1 ] ) + 1;
            accumArea += ( dx * dy );
            m_rectangleAreas.push_back( accumArea );
        }
    }

    vector<int> pick() {
        vector< int > ans( 2, 0 );
        const int maxAccumArea = m_rectangleAreas.back();
        const int rngRoll = rand() % maxAccumArea;
        auto it = upper_bound( m_rectangleAreas.begin(), m_rectangleAreas.end(), rngRoll );
        const int index = distance( m_rectangleAreas.begin(), it );
        const int prevAreaAccum = ( index > 0 ) ? *( it - 1 ) : 0;
        const vector< int > & targetRectangle = m_rectangles[ index ];
        const int startX = targetRectangle[ 0 ];
        const int startY = targetRectangle[ 1 ];
        const int endX = targetRectangle[ 2 ];
        const int diffArea = rngRoll - prevAreaAccum;
        const int dx = ( endX - startX ) + 1;
        // deal with the left over area to get a random point
        // treat this like a row major 2d array
        const int yOffset = diffArea / dx;
        const int xOffset = diffArea - ( yOffset * dx );
        ans[ 0 ] = startX + xOffset;
        ans[ 1 ] = startY + yOffset;        
        return ans;
    }
};
