class Solution(_rects: Array[Array[Int]]) {
  val rnd = scala.util.Random
  def getRnd(mx:Int) = rnd.nextInt.abs % mx
  val prefixSums = collection.immutable.SortedMap.from(
    _rects.map{r=>(r(2)-r(0)+1)*(r(3)-r(1)+1)}
    .foldLeft(List(0)){case (list,area) =>
      ((list.head) + area) +: list
    }.reverse.tail.zipWithIndex
  )
  // println(s"  prefixSums=$prefixSums")
  val sum = prefixSums.keys.last  // last key is max for sorted map
  def pick(): Array[Int] = {
    val r = _rects(prefixSums.iteratorFrom(getRnd(sum+1)).next._2)  // select rectangle
    Array(r(0)+getRnd((r(2)-r(0)+1)), r(1)+getRnd((r(3)-r(1)+1)))    // select points
  }
}
