class Solution {
public:
    map<int, vector<int>> domains;
    int total_area;
    Solution(vector<vector<int>>& rects) {
        int start=0;
        for(auto rect : rects)
        {
            vector<int> domain{rect[0], rect[1], rect[2]-rect[0]+1, rect[3]-rect[1]+1};
            domains[start]=domain;
            start+=domain[2]*domain[3];
        }
        total_area=start;
    }

    vector<int> pick() {
        int rand_index=rand();
        //while(rand_index>=(RAND_MAX/total_area*total_area)) rand_index=rand(); // this could make it more evenly distributed between 0~total_area
        rand_index%=total_area;
        auto it=domains.upper_bound(rand_index);
        int start_index=(--it)->first;
        vector<int> rect=it->second;
        int x=rect[0]+(rand_index-start_index)%rect[2];
        int y=rect[1]+(rand_index-start_index)/rect[2];
        return vector<int>{x, y};
    }
};
