class Solution {
private:
    vector<int>wts;
    vector<vector<int>>rects;

    int getArea(vector<int>& rect){
        return (rect[3] - rect[1] + 1) * (rect[2] - rect[0] + 1); 
    }

    vector<int> getRandom(vector<int>& rect){
        int x_diff = rect[2] - rect[0];
        int y_diff = rect[3] - rect[1];
        int x = rect[0] + rand() % (x_diff + 1);
        int y = rect[1] + rand() % (y_diff + 1);
        vector<int> randPoint;
        randPoint.push_back(x);
        randPoint.push_back(y);
        return randPoint;
    }

public:
    Solution(vector<vector<int>>& rects) : rects(rects) {
        int i, j;
        wts.push_back(getArea(rects[0]) - 1);
        for(i = 1; i < rects.size(); i++){
            j = wts[i - 1];
            wts.push_back(getArea(rects[i]) + j);
        }
    }

    vector<int> pick() {
        int i;
        int j = rand() % wts.back();
        auto it = upper_bound(wts.begin(), wts.end(), j);
        if(it == wts.begin()){
            i = 0;
        }
        else{
            auto prev = it - 1;
            if(*prev == j){
                i = prev - wts.begin();
            }
            else{
                i = it - wts.begin();
            }
        }
        return getRandom(rects[i]);
    }
};

