class Solution {
    constructor(rects) {
        this.rects = rects;
        this.rectsWeights = rects.map(([x1, y1, x2, y2]) => (x2 - x1 + 1) * (y2 - y1 + 1));
        this.rectsWeightsPrefixSum = [];
        for (let i = 0, curr = 0; i < this.rectsWeights.length; i++) {
            curr += this.rectsWeights[i];
            this.rectsWeightsPrefixSum.push(curr);
        }
    };


    pickRect() {
        const upperBound = (value, arr, left = 0, right = arr.length - 1) => {
            if (left === right) {
                return left;
            }
            const guess = Math.floor((right  + left) / 2);
            if (arr[guess] <= value) {
                return upperBound(value, arr, guess + 1, right);
            } else {
                return upperBound(value, arr, left, guess);
            }
        }
        const last = (arr) => arr[arr.length - 1];
        const randomWeightSum = Math.random() * last(this.rectsWeightsPrefixSum);
        const rectIndex = upperBound(randomWeightSum, this.rectsWeightsPrefixSum);
        return this.rects[rectIndex];
    }

    pick() {
        const [x1, y1, x2, y2] = this.pickRect();
        const x = x1 + Math.floor(Math.random() * (x2 - x1 + 1));
        const y = y1 + Math.floor(Math.random() * (y2 - y1 + 1));
        return [x, y];
    }
};
