class Solution {
    int[] weights;
    int[][] rects;
    Random rd;

    public Solution(int[][] rects) {
        this.weights = new int[rects.length];
        this.rects = rects;
        this.rd = new Random();

        for (int i = 0; i < rects.length; i++) {
            int[] rect = rects[i];

            int count = (1 + rect[2] - rect[0]) * (1 + rect[3] - rect[1]);

            weights[i] = (i == 0 ? 0 : weights[i - 1]) + count;
        }
    }

    public int[] pick() {
        int picked = 1 + rd.nextInt(weights[weights.length - 1]);

        int idx = findGreaterOrEqual(picked);

        return getRandomPoint(idx);
    }

    private int findGreaterOrEqual(int target) {
        int left = 0, right = weights.length - 1;
        while (left + 1 < right) {
            int mid = left + (right - left) / 2;
            if (weights[mid] >= target) {
                right = mid;
            } else {
                left = mid + 1;
            }
        }
        return weights[left] >= target ? left : right;
    }

    private int[] getRandomPoint(int idx) {
        int[] r = rects[idx];
        int left = r[0], right = r[2], bot = r[1], top = r[3];

        return new int[]{left + rd.nextInt(right - left + 1), 
                         bot + rd.nextInt(top - bot + 1)};
    }
}


