class Solution {
public:
    bool isValidBST(TreeNode* root, long int min = LONG_MIN, long int max = LONG_MAX) {
        if (!root) return true;

        if (root->val > min && root->val < max) {
            return true && isValidBST(root->left, min, root->val) && isValidBST(root->right, root->val, max);
        }

        return false;
    }
};
```


