        if (root == null) return true;
        if (min < root.val && root.val < max) {
            var leftResult = DFS(root.left, min, root.val);
            var rightResult = DFS(root.right, root.val, max);

            if (leftResult && rightResult) {
                return true;
            }
        }

        return false;
    }
}
