    public class Solution {
        public boolean isValidBST(TreeNode root) {
            if (root == null) {
                return true;
            }
            if (root.left == null && root.right == null) {
                return true;
            }
            return check(root, Integer.MAX_VALUE, Integer.MIN_VALUE);
