// Recursion solution - Using nodes
class Solution {
public:
    bool isValidBST(TreeNode* root) {
        return solve(root, NULL, NULL);
    }

    bool solve(TreeNode* root, TreeNode* max, TreeNode* min){
        if(root==NULL){
            return true;
        }

        if((min==NULL || root->val > min->val) && (max==NULL || root->val < max->val)){
            return solve(root->left, root, min) && solve(root->right, max, root);
        }
        return false;
    }
};


// Iterative solution
class Solution {
public:
    bool isValidBST(TreeNode* root) {
        if(root == NULL)
            return true;

        stack<TreeNode*> s;
        TreeNode *pre = NULL;

        while(root != NULL || !s.empty()){
            while(root != NULL){
                s.push(root);
                root = root->left;
            }
            root = s.top();
            s.pop();
            if(pre != NULL && root->val <= pre->val){
                return false;
            }
            pre = root;
            root = root->right;
        }
        return true;
    }
};

