 *         this.right = right;
 *     }
 * }
 */
class Solution {
    public TreeNode pre=null;
    public boolean isValidBST(TreeNode root) {

     if(root!=null){
         if(!isValidBST(root.left)) return false;
         if(pre!=null && root.val<=pre.val) return false;
         pre=root;
         return isValidBST(root.right);
     }   
     return true;
    }
}
