private:
	vector<int> tree;
public:
	void inOrder(TreeNode* root) {
	// if root value is null
		if (!root)
			return;
		inOrder(root->left);
		tree.push_back(root->val);
		inOrder(root->right);
	}

	bool isValidBST(TreeNode* root) {
		if (!root)
			return true;

		// getting the inorder traversal of the tree
		inOrder(root);
		for (int i=1; i<tree.size(); i++)
			if (tree[i] <= tree[i-1])
				return false;
		return true; 
	}
