You can check if binary tree to be a BST by checking if the the inorder traversal is in sorted (ascending) order. 

```
class Solution {
public:
    bool isValidBST(TreeNode* root) {
        TreeNode* prev = NULL;
        return isBST_inorder_iterative(root); 
    }

    bool isBST_inorder_recurse(TreeNode* p, TreeNode* &prev) {
        if(p == NULL) return true;

        if(!isBST_inorder_recurse(p->left, prev)) return false;
        if(prev != NULL && prev->val >= p->val) return false;
        prev = p;
        return isBST_inorder_recurse(p->right, prev);

    }

    bool isBST_inorder_iterative(TreeNode* p) {
        if(p == NULL) return true;
        stack<TreeNode*> st;
        TreeNode *curr = p;
        TreeNode *prev = NULL;

        while(curr != NULL || !st.empty()) {

            while(curr != NULL) {
                st.push(curr);
                curr = curr->left;
            }
            curr = st.top(); st.pop();
            if(prev != NULL && prev->val >= curr->val) return false;
            prev = curr;
            curr = curr->right;
        }
        return true;
    }

