        inorder(root);
        for (int i = 0; i < list.size(); i++) {
            if (list.get(i) >= list.get(i+1)){
                return false;
            }
        }
        return true;
    }

    public void inorder(TreeNode root){
        if (root == null){
            return;
        }
        inorder(root.left);
        list.add(root.val);
        inorder(root.right);
    }
}
