public boolean isValidBST (TreeNode root){
       Stack<TreeNode> stack = new Stack<TreeNode> ();
       TreeNode cur = root ;
       TreeNode pre = null ;       
       while (!stack.isEmpty() || cur != null) {         
         if (cur != null) {
           stack.push(cur);
           cur = cur.left ;
         } else {           
           TreeNode p = stack.pop() ;
           if (pre != null && p.val <= pre.val) {             
             return false ;
           }           
           pre = p ;             
           cur = p.right ;
         }
       }
       return true ; 
 }

