       	        {
       	            if (i == 0)
       	                return nums[0];
       	            if (i < 0)
       	                return 0;
       	            if (dp[i] != -1)
       	                return dp[i];
       	            int pick = nums[i] + Memoization(i - 2, nums, dp);
       	            int nonPick = 0 + Memoization(i - 1, nums, dp);
       	            return dp[i] = max(pick, nonPick);
       	        }
