       	    {
       	        int n = nums.size();
       	        vector<int> dp(n, -1);
       	        return Memoization(n-1, nums, dp);
       	    }

       	//Tabulation(Unoptimized)
 int rob(vector<int> &nums)
        {
            int n = nums.size();
            vector<int> dp(n, -1);
            dp[0] = nums[0];
            for (int i = 1; i < n; i++)
            {
                int pick = nums[i];
                if (i > 1)
                    pick = pick + dp[i - 2];
                int notPick = 0 + dp[i - 1];
                dp[i] = max(pick, notPick);
            }
            return dp[n - 1];
        }

   	//Tabulation(Optimized)
    int rob(vector<int> &nums)
    {
        int n = nums.size();
        int prev1 = nums[0];
        int prev = 0;
        int curri = 0;
        for (int i = 1; i < n; i++)
        {
            int pick = nums[i];
            if (i > 1)
                pick = pick + prev;
            int notPick = 0 + prev1;
            curri = max(pick, notPick);
            prev = prev1;
            prev1 = curri;
        }
        return prev1;
    }
};
