class Solution {
    public int rob(int[] nums) {
        int n=nums.length;
        if(n==0){
            return 0;
        }
        if(n==1){
            return nums[0];
        }
        if(n==2){
            return Math.max(nums[0],nums[1]);
        }
		/*above are just  for special conditions when array length is less than 2*/

        int[] dp=new int[n]; //creating array
        Arrays.fill(dp,-1);
        dp[n-1]=nums[n-1];
        dp[n-2]=nums[n-2];

        for(int i=0;i<nums.length;i++){
        helper(dp,nums,i);
        }
        return  Arrays.stream(dp).max().getAsInt();
    }
    public int helper(int[] dp,int[] nums,int i){
        if(dp[i]!=-1)
            return dp[i];
        int max=-1;
        for(int j=i+2;j<nums.length;j++){
            dp[j]=helper(dp,nums,j);
            if(max<dp[j])
                max=dp[j];
        }
        dp[i]=nums[i]+max;
        return dp[i];
    }
}
```

