let rob = A => {
    let N = A.length;
    let dp = Array(N + 2).fill(0);
    for (let i = N - 1; 0 <= i; --i) {
        let take = dp[i + 2] + A[i],
            skip = dp[i + 1];
        dp[i] = Math.max(take, skip);
