class Solution {
public:
    int find(int idx,vector<int>& nums,vector<int>& dp)
    {
        if(idx==0) return nums[0];
        if(dp[idx] != -1) return dp[idx];

        int not_pick = 0 + find(idx-1,nums,dp);
        int pick = nums[idx];
        if(idx-2 >= 0)
        {
            pick += find(idx-2,nums,dp);
        }

        return dp[idx] = max(pick,not_pick);
    }

    int rob(vector<int>& nums) 
    {
        int n = nums.size();
        if(n==1) return nums[0];
        vector<int> dp(n,-1);
        return find(n-1,nums,dp);
    }
};

