class Solution {
    public int rob(int[] nums) {
        int[] dp = new int[nums.length+1];
        Arrays.fill(dp, -1);
        int res = houseRob(nums, 0, nums.length, dp);
        return(res);
    }

    public static int houseRob(int[] nums, int curr, int n, int[] dp){
        if(curr >= n){ 
            return 0;
        }
        if(dp[curr] != -1) return dp[curr];

        // First time calculation
        int rob = nums[curr] + houseRob(nums, curr+2, n, dp);
        int doNot = houseRob(nums, curr+1, n, dp);

        return dp[curr] = Math.max(rob, doNot);
    }
}

