class Solution {
    public int rob(int[] nums) {

        int prev=nums[0];
        int secPrev=0;
        for(int i=1;i<nums.length;i++)
        {
            int pick=nums[i]+secPrev;
            int notPick=prev;
            int curr=Math.max(pick,notPick);
            secPrev=prev;
            prev=curr;
        }
        return prev;

