class Solution {
public: 
    int dp[102]; 

    int rob(vector<int>& nums) {  

        memset(dp, -1, sizeof(dp)); 

        return knapSack(nums, nums.size());

    } 

    int knapSack(vector<int> &nums, int n)  { 

        if (n <= 0) { 
            return 0;
        } 

        if (dp[n] != -1) { 
            return dp[n];
        }

        return dp[n] = max(nums[n-1] + knapSack(nums, n-2), knapSack(nums, n-1));
    }
};
