class Solution {
    public int rob(int[] nums) {

        // Since we only refer to previous two grids, we can carry out space optimization

        // refer to dp[i-2]
        int take_prev_2 = 0;

        // refer to dp[i-1]
        int take_prev_1 = 0;

        int profit = 0;

        for(int value : nums){

            profit = Math.max(take_prev_2 + value, take_prev_1 + 0);
            take_prev_2 = take_prev_1;
            take_prev_1 = profit;
        }

        return profit;
    }
}
