//Approach 1: 1D DP


class Solution {
 public:
  int rob(vector<int>& nums) {
    if (nums.empty())
      return 0;
    if (nums.size() == 1)
      return nums[0];

    // dp[i] := max money of robbing nums[0..i]
    vector<int> dp(nums.size());
    dp[0] = nums[0];
    dp[1] = max(nums[0], nums[1]);

    for (int i = 2; i < nums.size(); ++i)
      dp[i] = max(dp[i - 1], dp[i - 2] + nums[i]);

    return dp.back();
  }
};

//Approach 2: O(1) DP Bottom UP 

class Solution {
public:
    int rob(vector<int>& a) {

        if(a.size() ==0)return 0;
        if(a.size() ==1)return a[0];
        if(a.size() ==2)return max(a[0],a[1]);

        vector<int>loot(a.size());

        loot[0] =a[0];
        loot[1] = max(a[0],a[1]);

        for(int i=2;i<a.size();i++){
            loot[i] = max(loot[i-2]+a[i],loot[i-1]);
        }

          return loot[a.size()-1];

    }
};


//second

class Solution {
 public:
  int rob(vector<int>& nums) {
    int prev1 = 0;  // dp[i - 1]
    int prev2 = 0;  // dp[i - 2]

    for (const int num : nums) {
      const int dp = max(prev1, prev2 + num);
      prev2 = prev1;
      prev1 = dp;
    }

    return prev1;
  }
};

 `class Solution {
public:
    int recursive(int idx, vector<int> &nums, int sum){
        sum = 0;

        if(idx == 0){
            sum = sum + nums[0];
            return sum;
        }

        if(idx<0){ return 0; }

        int pick = sum + nums[idx] + recursive(idx-2, nums , sum);

        int not_pick = sum + recursive(idx-1, nums, sum);

        return max(pick,not_pick);
    }
    int rob(vector<int>& nums) {
        int n = nums.size();
        int sum = recursive(n-1, nums , 0);

        return sum;

    }
};`

