class Solution:

    def __init__(self, nums: List[int]):
        self.nums = nums

    def pick(self, target: int) -> int:
        count = 0

        s = None

        for i, num in enumerate(self.nums):
            if num != target:
                continue

            count += 1
            if random.uniform(0, 1) <= 1 / count:
                s = i

        return s
