    private Random random;
    private int[] nums;

    public Solution(int[] nums) {
        random = new Random();
        this.nums = nums;
    }

    public int pick(int target) {
        // Declare candidate as -1
        int candidate = -1;

        // Initialize index as 0
        int index = 0;

        // Iterate nums
        for (int i = 0; i < nums.length; i++) {
            // If num[i] equal to target
            if (nums[i] == target) {
                // Get random from 0 to index inclusive
                int randomValue = random.nextInt(index + 1);
                // If random equal to 0, i as candidate
                if (randomValue == 0) {
                    candidate = i;
                }
                // Increase index
                index++;
            }
        }

        return candidate;
    }
