class Solution {
public:
    map<int, deque<int>> m;
    Solution(vector<int>& nums) 
    {
        for(int i=0;i<nums.size();i++)
        {
            m[nums[i]].push_back(i);
        }
    }
    int pick(int target) 
    {
        int ans=m[target][0];
        deque<int> d = m[target];
        int k= d[0];
        d.pop_front();
        d.push_back(k);
        m[target]=d;
        return ans;
    }
};
