        // iterate through diagnol
        for(int pos=0; pos<retObject.length ; pos++ ){
            retObject[pos]=matrix[row][col]; 
            // If diagnoal level is Even
            if( (row+col) % 2 ==0){
                // If col reaches last col then  move down row increased;
                if(col == cols-1) row++;
                // If row reaches last row
                else if(row == 0) col++;
