```
    vector<int> findDiagonalOrder(vector<vector<int>>& mat) {
        vector<int> result;
        // we define two directions, 1 and -1
        // if current cell is (r, c)
        // for direction 1, next iteration cell is (r - 1, c + 1)
        // for direction -1, next iteratoin cell is (r + 1, c - 1)
        compute(mat, 0, 0, 1, result);
        return result;
    }

    void compute(vector<vector<int>>& mat, int r, int c, int dir, vector<int>& result) {
        int rows = mat.size();
        int columns = mat[0].size();
        result.push_back(mat[r][c]);
        if (result.size() == rows * columns) {
            return;
        }
        if (dir == 1 && c == columns - 1) {
            // if direction is 1 and current we are at the last column, then the only way is to go down
            compute(mat, r + 1, c, -1, result);
        } else if (dir == 1 && r == 0) {
            // if direction is 1 and we are in the first row and not the last column, then we need to go right
            compute(mat, r, c + 1, -1, result);
        } else if (dir == -1 && r == rows - 1) {
            // if direction is -1 and we are at the last row, then we have to go right
            compute(mat, r, c + 1, 1, result);
        } else if (dir == -1 && c == 0) {
            // if direction is -1 and we are at the first column and it's not the last row, then we need to go down
            compute(mat, r + 1, c, 1, result);
        } else {
            // safe to move along the direction to the next cell
            compute(mat, r - dir, c + dir, dir, result);
        }
    }
