class Solution {
    public int[] findDiagonalOrder(int[][] mat) {
        if (mat.length == 0) return new int[0];

        int i = 0;
        int j = 0;
        int m = mat.length;
        int n = mat[0].length;
        int[] result = new int[m * n];

        for (int k = 0;k < result.length;k++){
            result[k] = mat[i][j];
            //all sum diagonal values will be even 
            // left to right [0,0], [1,1], [2,2]
            // right to left [2,0], [1,1], [0,2]
            if ((i + j) % 2 == 0){ // moving up
                if (j == (n-1)) i++; //reached the column bound, move the row
                else if(i == 0) j++;    //reaching up and we have to move the column
                else {                  //from left, going up decrement row, increment column
                    i--; 
                    j++;
                }
            }
            else{
                if (i == (m-1)) j++;    //hit the row bound, increment column
                else if(j == 0) i++;   // column bound, increment row
                else {                 //from right, going down increment row, decrement column
                    i++;
                    j--;
                }
            }
        }
        return result;
    }
}
