public int[] findDiagonalOrder(int[][] matrix) {
	if (matrix.length == 0) return new int[]{};
	int rowsNum = matrix.length - 1, columnsNum = matrix[0].length - 1, i = 0;
	int[] result = new int[(rowsNum + 1) * (columnsNum + 1)];
	for (int diagonal = 0; diagonal <= rowsNum + columnsNum; diagonal++) {
		if (diagonal % 2 == 0) {
			// direction top_right
			for (int r = Math.min(diagonal, rowsNum), c = Math.max(0, diagonal - rowsNum); r >= 0 && c <= columnsNum; r--, c++) {
				result[i++] = matrix[r][c];
			}
		} else {
			// direction bottom_left
			for (int r = Math.max(0, diagonal - columnsNum), c = Math.min(diagonal, columnsNum); r <= rowsNum && c >= 0; r++, c--) {
				result[i++] = matrix[r][c];
			}
		}
	}
	return result;
}
