/**
 * @param {number[][]} matrix
 * @return {number[]}
 */
var findDiagonalOrder = function(matrix) {
    if (!matrix.length) return matrix;

	const depth = matrix.length;
    const width = matrix[0].length;

    const result = [];
    let i = 0;
    let j = 0;
    let goUp = true; // direction marker


    while (result.length < depth * width) { // while result is not filled up with (depth * width) elements
        result.push(matrix[i][j]);

        if (goUp) { // if direction is top-right
            if (j === width - 1) { // if last coloumn - move one row down and change direction
                i++;
                goUp = false;
            } else if (i === 0) { // else if first row - move one coloumn right and change direction
                j++;
                goUp = false;
            } else { // else just move diagonally to top-right
                i--;
                j++;
            }
        } else { // if direction is bottom-left
            if (i === depth - 1) { // if last row - move right and change direction
                j++;
                goUp = true;
            } else if (j === 0) { // else if first coloumn - move down and change direction
                i++;                   
                goUp = true;
            } else { // else just move diagonally to bottom-left
                i++;
                j--;
            }
        }
    }

    return result;
};
