class Solution {
    public int[] findDiagonalOrder(int[][] matrix) {
        if(matrix.length == 0) {
            return new int[0];
        }
        int m = matrix.length;
        int n = matrix[0].length;
        int[] res = new int[m*n];
        int i = 0, j = 0, k = 0;
        int cnt = 0, diags = m + n - 1;
        while(cnt < diags) {
            cnt++;
            if(cnt % 2 != 0) { // traverse up on odd diagonals
                while(i >= 0 && j < n) {
                    res[k++] = matrix[i--][j++];
                }
            } else {
                while(j >= 0 && i < m) {
                    res[k++] = matrix[i++][j--];
                }
