            if(i == m) {
                i = m - 1;
                j += 2;
            } else if(j == n) {
                j = n - 1;
                i += 2;
            } else if(i < 0) {
                i = 0;
            } else if(j < 0) {
                j = 0;
            } 
        }
        return res;
    }
}
